```{ojs}
// Import necessary modules
import { Table, Inputs } from "@observablehq/inputs";

// Load the CSV data and ensure Student_ID is a string with leading zeros
data = FileAttachment("homework_scores.csv")
  .csv({ typed: true })
  .then(rows =>
    rows.map(row => ({
      ...row,
      Student_ID: String(row.Student_ID).padStart(9, '0')
    }))
  );

// Create a styled container for input and button (aligned in one row)
viewof inputContainer = html`<div style="
  display: flex; 
  align-items: center; 
  gap: 10px;
  margin-bottom: 15px;
">`;

// Define the input box for Student ID
let studentID = Inputs.text({
  label: "ðŸ” Enter Student ID:",
  placeholder: "e.g., 012345667",
  style: {
    fontSize: "16px",
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    width: "250px"
  }
});

// Define the stylized confirm button
let confirmButton = Inputs.button("âœ” Confirm", {
  style: {
    fontSize: "16px",
    padding: "10px 20px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background-color 0.3s"
  },
  onmouseover: function () { this.style.backgroundColor = "#45a049"; },
  onmouseout: function () { this.style.backgroundColor = "#4CAF50"; }
});

// Append input and button to the container
inputContainer.append(studentID, confirmButton);

// Filter the data based on the entered student ID
filteredData = {
  if (confirmButton) {
    return data.filter(d => d.Student_ID === studentID);
  }
  return [];
};

// Display the filtered data in a table with custom styling
Table(filteredData, {
  columns: ["Student_ID", "Name", "Bonus", "HW1_Grade", "Total"],
  style: {
    width: "100%",
    borderCollapse: "collapse",
    fontSize: "16px"
  }
});

```
