// Import necessary modules
import { Table, Inputs } from "@observablehq/inputs"

// Load the CSV data and convert Student_ID to string
data = FileAttachment("homework_scores.csv").csv({typed: true}).then(rows => 
  rows.map(row => ({...row, Student_ID: String(row.Student_ID).padStart(9, '0')}))
)

// Create an input box for the student ID
viewof studentID = Inputs.text({
  label: "Enter Student ID",
  placeholder: "e.g., 012345667"
})

// Create a button to confirm the input
viewof confirmButton = Inputs.button("Confirm")

// Filter the data based on the entered student ID
filteredData = {
  if (confirmButton) {
    return data.filter(d => d.Student_ID === studentID)
  }
  return []
}

// Display the filtered data in a table
Table(filteredData, {columns: ["Student_ID", "Name", "Bonus", "HW1_Grade", "Total"]})
```
